textDescriptions.absorbance:
material: GaAs, n: adachi_simple, d: 90, k/n: 0.0;
material: AlGaAs, n: adachi_simple, d: 110, k/n: 0.0, cAl: 0.35;

x30
layer: excitonic,
medium: { material: GaAs, n: adachi_T, k/n: 0.0 },
exciton: { w0: 1.7, G0: 0.0005, G: 0.01 },
d: 10;

material: AlGaAs, n: adachi_simple, d: 100, k/n: 0.0, cAl: 0.35;
material: custom, d: 90, n: {
  fun f(x) = sin(x * 0.1) * 0.5
  fun g(x) = cos(x * 0.1) * 0.5
  return (f(x), g(x))
};

