textDescriptions.permittivity:
material: custom, d: 90, eps: {

	val v = Pi * 0.05
 	fun f(a, b) = sin(a * b) * 0.5
	fun g(a) = cos(a * 0.15) * 0.5
	return (f(x, v), g(x))

};

